mapper.select:
SELECT A.*, IFNULL(M.nickname, "삭제된_회원") AS extra__writerName FROM article AS A LEFT JOIN member AS M ON A.memberId = M.id WHERE A.boardId = #{boardId}
AND ( A.title LIKE CONCAT('%', #{searchKeyword}, '%') OR A.body LIKE CONCAT('%', #{searchKeyword}, '%') )
AND A.title LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.body LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY A.id DESC LIMIT #{limitFrom}, #{limitTake};

mapper.update:
UPDATE article
SET updateDate = NOW(),
title =
#{title},
body = #{body}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*) AS
likeCount
FROM checkLike
WHERE typeCode=#{code}
AND typeId=#{articleId}
AND checkLike=1

SELECT COUNT(*) AS
likeCount
FROM checkLike
WHERE typeCode=#{code}
AND typeId=#{articleId}
AND memberId=#{memberId}
AND checkLike=1

